<script>
    const emeletSelect = document.getElementById('emelet');
    const lakasSelect = document.getElementById('lakas');
    const options = lakasSelect.options;

    document.getElementById('emeletszeru').value = document.getElementById('emelet').value;
    document.getElementById('lakasszeru').value = document.getElementById('lakas').value;


    document.getElementById('emelet').addEventListener('change', function () {
        document.getElementById('emeletszeru').value = this.value;
    });

    document.getElementById('lakas').addEventListener('change', function () {
        document.getElementById('lakasszeru').value = this.value;
    });
    function Lakas() {
        const selectedEmelet = emeletSelect.value;
        for (let i = 0; i < options.length; i++) {
            if (selectedEmelet == '1' || selectedEmelet == '6') {
                if (options[i].value == '1' || options[i].value == '4') {
                    options[i].disabled = false;
                } else {
                    options[i].disabled = true;
                }
            } else {
                options[i].disabled = false;
            }
        }
        lakasSelect.value = '1';
    }
    emeletSelect.addEventListener('change', Lakas);
    Lakas();


</script>
